mapper.update:
update member set mem_name=#{me.mem_name}, mem_birth1=#{me.mem_birth1},mem_birth2=#{me.mem_birth2},mem_birth3=#{me.mem_birth3}, email_id=#{me.email_id},email_domain=#{me.email_domain},
mem_pwd=#{me.mem_pwd},
mem_phone01=#{me.mem_phone01},mem_phone02=#{me.mem_phone02},mem_phone03=#{me.mem_phone03} where mem_no=#{me.mem_no}

UPDATE member 
	SET mem_nickname=#{mem_nickname}, profile_photo=#{profile_photo},
		profile_cont=#{profile_cont}, mem_keyword=#{mem_keyword},  mem_portfolio=#{mem_portfolio}
		where mem_id=#{mem_id}
